#include <iostream>
using namespace std;
int main()
{
 cout<<"bool:\n"<<sizeof(bool)<<endl<<endl<<"char:\n"<<sizeof(char)<<endl<<endl<<"signed char:\n"<<sizeof(signed char)<<endl<<endl<<"unsigned char:\n"<<sizeof(unsigned char)<<endl<<endl<<"wchar_t:\n"<<sizeof(wchar_t)<<endl<<endl<<"short:\n"<<sizeof(short)<<endl<<endl<<"unsigned short:\n"<<sizeof(unsigned short)<<endl<<endl<<"int:\n"<<sizeof(int)<<endl<<endl<<"unsigned int:\n"<<sizeof(unsigned int)<<endl<<endl<<"long:\n"<<sizeof(long)<<endl<<endl<<"unsigned long:\n"<<sizeof(long)<<endl<<endl<<"long long int:\n"<<sizeof(long long int)<<endl<<endl<<"unsigned long long int:\n"<<sizeof(unsigned long long int)<<endl<<endl
 <<"float:\n"<<sizeof(float)<<endl<<endl<<"double:\n"<<sizeof(double)<<endl<<endl<<"long double:\n"<<sizeof(long double)<<endl;
}
